                  <label for="InputAddress">Address:</label>
                  <br/>
                  <input name="EmpAddress" type="text" id="InputAddress" aria-describedby="addressHelp" placeholder="Enter Address" [(ngModel)]="Address" required>
              </div>
              <button type="submit" id="subButton" class="buttons">Submit</button>
              <button (click)="closeAddAssocModal()" class="buttons"> close</button>
            </form>
          </div>
    </div>
    <div *ngIf="isEditAssocModal">
      <form (ngSubmit)="editAssoc($event)" #editForm>
        <div class="form-group">
          <button (click)="closeEditAssocModal()">close </button>
          <h2>Edit Associate</h2>
          <div class="form-group">
            <label for="editedName">Name:</label>
            <br/>
            <input name="EditName" type="text" id="editedName" placeholder="Edit Name" [(ngModel)]="Name" required>
          </div>
          <div class="form-group">
            <label for="editedPass"> Password:</label>
            <br/>
            <input name="EditPassword" type="text" id="editedPass" placeholder="Edit Password" [(ngModel)]="Password" required>
          </div>
          <div class="form-group">
            <label for="editedAddress"> Address:</label>
            <br/>
            <input name="EditAddress" type="text" id="editedAddress" placeholder="Edit Address" [(ngModel)]="Address" required>
          </div>
          <button type="submit" class="buttons">Save Changes</button>
        </div>
      </form>
    </div>

    <ng-container *ngIf="isMainScreen">
      <div class="table-container">
        <table class="main-table">
            <thead>
                <tr>
                    <td>ID</td>
                    <td>Name</td>
                    <td>Password</td>
                    <td> Sales Commission</td>
                    <td> Address</td>
                    <td> </td>
                </tr>
            </thead>
            <tbody id="myTable">
                <tr *ngFor="let rows of FilteredAssoc">
                    <td>{{rows.ID}}</td>
                    <td>{{rows.Name}}</td>
                    <td>{{rows.Password}}</td>
                    <td>{{rows.SalesCommision | number: '1.2-2'}}</td>
                    <td>{{rows.Address}}</td>
                    <td>
                    <button class="buttons" (click)="editAssocModal(rows)">Edit</button>
                    </td>
                    <td>
                    <button class="buttons" (click)="delAssoc(rows.ID)">Delete</button>
                    </td>
                </tr>
            </tbody>
        </table>   

        <table class="main-table">
            <thead>
                <tr>
                    <td>ID</td>
                    <td>AssocID</td>
                    <td>CustID</td>
                    <td>Customer Name</td>
                    <td> QuoteDate</td>
                    <td> Status</td>
                    <td> Total</td>
                    <td> Discount</td>
                </tr>
            </thead>
            <tbody id="myTable">
                <tr *ngFor="let rows of FilteredQuote">
                  
                    <td>{{rows.ID}}</td>
                    <td>{{rows.AssocID}}</td>
                    <td>{{rows.CustID}}</td>
                    <td>{{rows.CustName}}</td>
                    <td>{{rows.QuoteDate}}</td>
                    <td>{{rows.Status}}</td>
                    <td>{{rows.Total}}</td>
                    <td>{{rows.Discount}}</td>
                    <td>
                      <button class="buttons" (click)="ViewQuoteModal()">View</button>
                    </td>
                </tr>
            </tbody>
        </table>
      </div>
    </ng-container>

    <div *ngIf="isViewQuoteModal" class="modal">
      <div class="modal-content">
        <form (ngSubmit)="viewQuote($event)" #myForm>
        <button class="buttons"(click)="closeViewQuoteModal()">Close</button>
        <h2>View Quote</h2>
        <div class="form-group">
          <label for="viewQuoteID">ID:</label>
          <br/>
          <input name="viewQuoteID" type="text" id="viewQuoteID" [(ngModel)]="selectedQuote.ID" readonly>
        </div>
        <div class="form-group">
          <label for="viewQuoteAssocID">AssocID:</label>
          <br/>
          <input name="viewQuoteAssocID" type="text" id="viewQuoteAssocID" [(ngModel)]="selectedQuote.AssocID" readonly>
        </div>
          </form>
      </div>
    </div>
  </body>
</html>
