import { Component } from '@angular/core';
import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { FormsModule } from '@angular/forms'; 
import { HomeComponent } from './home/home.component';
import * as $ from 'jquery';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent {
  title = 'CSCI467 Group Project';
  //This function is used to pop up our login form
  Showlogin(): void{

    const ScaleIn = [
      { transform: "scale(0)" },
      { transform: "scale(1)" },
    ];
    const ScaleInTiming = {
      duration: 1000,
      iterations: 1,
    };
    const forDisplay = document.getElementById("LoginForm");
    const logButton = document.getElementById("OpenLogin");
    const closeButton = document.getElementById("CloseLogin");
    
    if(forDisplay != null && logButton != null && closeButton != null){
      forDisplay.animate(ScaleIn, ScaleInTiming);
      forDisplay.style.visibility = "visible";
      closeButton.style.display="flex";
    }
    
  }
}
<html>
  <head>
    <title>CSCI 467 Group 3B</title>
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN" crossorigin="anonymous">
  </head>
  <body>
    <nav class="nav">
      <ul class="menu">
        <li><a href="">Home</a></li>
        <li><a href="/quote">Create a Quote</a></li>
        <li><a href="/admindash">Admin</a></li>
      </ul>
      <div class="hamburger">
        <span class="bar"></span>
        <span class="bar"></span>
        <span class="bar"></span>
      </div>
      <button id="OpenLogin" class="btn btn-primary" (click)="Showlogin()">Login</button>
    </nav>
    <div class="container">
      <router-outlet></router-outlet>
    </div> 
    <footer class="footer">
      <div class="footerList">
        <ul>
          <li>Filler</li>
          <li>Filler2</li>
        </ul>
      </div>
      <p class="TradeMark">© Designed and Created By: Angelo LeDonne, Caleb Patsch, Fabien Cornejo, Alan Garcia</p>
    </footer> 
  </body>
</html>
