import { Component, ElementRef, Renderer2, ViewChild, OnInit } from '@angular/core';
import { Router, NavigationEnd,ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-pocreate',
  templateUrl: './pocreate.component.html',
  styleUrls: ['./pocreate.component.css']
})
export class PocreateComponent {
  responseFromPHP: any;
  selectOptions: any[] = [];
  SelectedVal: any;
  EmpName: any;
  savedAssoc: any;

  constructor(private http: HttpClient, private router: Router) { }

  ngOnInit() {
    this.http.get('https://phpapicsci467.azurewebsites.net/php_script/selectQuote.php').subscribe((response: any) => {
      this.selectOptions = response;
    });
  }

  Datacheck(): void
  {
    //Stored Associate Name
    var AssocName = localStorage.getItem('AssocName');
    if(AssocName !== null){
      this.EmpName = JSON.parse(AssocName);
    }
    //Stored Associate ID
    var savedAssoc = localStorage.getItem('CurrentAssoc');
    if(savedAssoc !== null){
      this.savedAssoc = JSON.parse(savedAssoc);
      console.log(savedAssoc);
    }else{
      this.router.navigateByUrl('/');
      setTimeout(function(){
        alert("Error: User not found, please Login");
      }, 200);
    }
  }

  private quoteURL='https://phpapicsci467.azurewebsites.net/php_script/';

  RetriveData() : void{
    console.log(this.SelectedVal);

    const quoteData={quoteID: this.SelectedVal}
    this.http.post(this.quoteURL,)
  }
  count: number=2;

  AddRow() : void{
    var htmlContent = `<div class="col col-lg"> 
                          <div class="p-3 border bg-dark"><input id="Item${this.count}" type="text"></div> 
                          </div> 
                          <div class="col col-md-auto"> 
                              <div class="p-3 border bg-dark"><input id="Qty${this.count}" type="number"></div>
                          </div> 
                          <div class="col col-md-auto"> 
                              <div class=" p-3 border bg-dark"><input id="Price${this.count}" type="number" step="0.01"></div> 
                          </div>
                       </div>`;

    // Create a new HTML element
    //const newElement = this.renderer.createElement('hr');
    //const newElement2 = this.renderer.createElement('div');
    //newElement2.className = 'row';
    //newElement.style.borderWidth = '5px';
    //newElement.style.color = 'rgb(0,0,0)';
    //newElement.style.backgroundColor = 'black';
    //newElement2.innerHTML = htmlContent;

    // Append the new element as the last child of the div
    //this.renderer.appendChild(this.container.nativeElement, newElement);

    //this.renderer.appendChild(this.container.nativeElement, newElement2);

    this.count++;
  }
}
<html>
<head>
    <link rel="stylesheet" href="pocreate.component.css">
    <title>group</title>
</head>
<body>

    <!--Code Below is for Drop Down Menu-->
    <select class="item2" [(ngModel)]="SelectedVal" (ngModelChange)="RetriveData()">
        <option value="">Select a quote</option>
        <option *ngFor="let option of selectOptions" [value]="option.ID">Quote #: {{ option.ID }}, Customer Name: {{option.CustName}}</option>
    </select> 
    <!--End of Drop Down Menu Code-->

    <!--Code below is  for table-->

    <table class="main-table">
        <thead>
            <tr>
                <td>Name</td>
                <td>Description</td>
                <td>Quantity</td>
                <td>Total</td>
            </tr>
        </thead>
        <tbody id="myTable">
            <tr>

            </tr>
        </tbody>
        <tfoot>
            <tr>
                <td><label for="dscnt">Final Discount:</label></td>
                <td><input type="number" id="dscnt" placeholder="Enter Number"></td>
                <td colspan="2"><button type="button">Enter</button></td>
            </tr>
        </tfoot>
    </table>
</body>  
</html>
