import { Component } from '@angular/core';
import { ActivatedRoute, NavigationEnd, Router } from '@angular/router';
import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { FormsModule } from '@angular/forms'; 
import { HomeComponent } from './home/home.component';
import * as $ from 'jquery';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent {

  isHomeComponent = false;
  isQuoteComponent = false;

  constructor(private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.route.url.subscribe(url => {
      this.isHomeComponent = url[0].path === '/';
      this.isQuoteComponent = url[0].path === 'quote';

      console.log("Hello World" + "." + url[0].path);
      // Add similar conditions for other components/pages
    });
  }

  title = 'CSCI467 Group Project';

}
<html>
  <head>
    <title>CSCI 467 Group 3B</title>
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN" crossorigin="anonymous">
  </head>
  <body>
      
    <app-nav-bar></app-nav-bar>  
    <router-outlet></router-outlet> 
    <footer class="footer">
      <p class="TradeMark">© Designed and Created By: Angelo LeDonne, Caleb Patsch, Fabien Cornejo, Alan Garcia</p>
    </footer> 
  </body>
</html>
