import { Component, ElementRef, Renderer2, ViewChild, OnInit } from '@angular/core';


@Component({
  selector: 'app-quote',
  templateUrl: './quote.component.html',
  styleUrls: ['./quote.component.css']
})


export class QuoteComponent {

  @ViewChild('container', { static: true }) myDiv: ElementRef;

  constructor(private renderer: Renderer2) { }

  insertHTMLWithClass() {
    // Create a new div element
    const newDiv = this.renderer.createElement('div');
    newDiv.innerHTML = '<p>New content goes here</p>';

    // Add a class to the new div
    this.renderer.addClass(newDiv, 'newDivClass');

    // Append the new div to the existing div
    this.renderer.appendChild(this.myDiv.nativeElement, newDiv);
  
  }

  AddRow() : void{

    var htmlContent = '<div class="col col-lg"> <div class="p-3 border bg-dark"><input id="Item" type="text"></div> </div> <div class="col col-md-auto"> <div class="p-3 border bg-dark"><input id="Qty" type="number"></div></div> <div class="col col-md-auto"> <div class=" p-3 border bg-dark"><input id="Price" type="number" step="0.01"></div> </div>';

    var div = document.getElementsByClassName("container");

    var newRow = document.createElement("div");
    newRow.innerHTML = htmlContent;

    div.appendChild(newRow);
  }

}
