    return this.quoteForm.get('rows').controls;
  }
  /* Controls for Secret Notes rows in Responsive Form */
  get SecretNotesControls() {
    return this.quoteForm.get('SecretNotes').controls;
  }
  /* This function will add another row to the current Quote */
  addRow() {
    const newRow = this.formBuilder.group({
      ID: this.maxLineID,
      Item: '',
      Qty: 0,
      Price: 0.0,
      isNew: true,
      isDeleted: false
    });
    this.maxLineID;
    this.calculateRunningTotal();
    this.quoteForm.get('rows').push(newRow);
  }
  fillRows() {
    for (let line of this.selectedQuoteLines) {
      if (line['SecretFlag'] == 'N') {
        this.populateRow(line);
      } else {
        this.populateSecretNote(line);
      }
      this.maxLineID = line['LineID'];
    }
    this.calculateRunningTotal();
    this.maxLineID++;
    //this.addRow();
    //this.calculateRunningTotal();
  }

  populateRow(line) {
    const newRow = this.formBuilder.group({
      ID: line['LineID'],
      Item: line['RowDesc'],
      Qty: line['RowQty'],
      Price: line['RowPrice'],
      isNew: false,
      isDeleted: false
    });
    this.quoteForm.get('rows').push(newRow);
  }
  populateSecretNote(line) {
    this.showSecretNote = true;
    const secretNote = this.formBuilder.group({
      SecretNote: line['RowDesc'],
      isNew: false,
      isDeleted: false
    });
    this.quoteForm.get('SecretNotes').push(secretNote);
    this.NoteCounter++;
  }
  createRow() {
    return this.formBuilder.group({
