  }
  delAssoc() {
    const id = this.ID;
    const confirmation = confirm('Are you sure you want to delete this associate?');
    if (confirmation) {
      // Call your delete API endpoint with the specific ID  
      this.http.post('https://phpapicsci467.azurewebsites.net/php_script/deleteAssociate.php', {
        id
