/*
 * This Function Will Reset the Assoc Table for the Next Search
*/
ResetAssoc(): void {
  this.FilteredAssoc = this.assoc;
}
  

deleteAssocUrl="https://phpapicsci467.azurewebsites.net/php_script/deleteAssociate.php";
delAssoc(): void {
  const id = this.ID;
  const confirmation = confirm('Are you sure you want to delete this associate?');
  if (confirmation) {
    // Call your delete API endpoint with the specific ID  
    this.http.post('https://phpapicsci467.azurewebsites.net/php_script/deleteAssociate.php', { id }).subscribe((response: any) => {    
      if (response.success) {
        location.reload();   
      }else {        
        alert('Error deleting associate: ' + response.message);      
      }
      
    });
  }
}



// New method for searching associates
SearchAssoc(): void {
  let FilteredData = [];
  this.FilteredAssoc = this.assoc; // Reset to show all data
  if(this.searchTerm === ""){
    this.FilteredAssoc = this.assoc; // Reset to show all data
  }else{
    for(let i = 0; i < this.assoc.length; ++i){
  
      let row = this.assoc[i];
  
      for(const key in row){
        let MatchedRow = row;
        if(row[key].toLowerCase().includes(this.searchTerm.toLowerCase())){
          console.log(row);
          FilteredData.push(MatchedRow);
          break;
        }
      
      }
    }
    this.FilteredAssoc = FilteredData;
  }

}

/*
  Reset Quote Function
*/
ResetQuote(): void{
  this.FilteredQuote = this.quote;
}


// filters quote table by status
statusFilter(): void {
  const url = 'https://phpapicsci467.azurewebsites.net/php_script/StatusFilter.php';
  const params = { status: this.selectedStatus };

  if(this.selectedStatus == 'A'){
    this.ResetQuote();
  }else{
    this.http.post(url, params).subscribe((response: any) => {
      this.FilteredQuote = response;
    });
  }
}

searchQuote (): void {
  let FilteredData = [];
  this.FilteredQuote = this.quote; // Reset to show all data
  if(this.searchTerm2 === ""){
    this.FilteredQuote = this.quote; // Reset to show all data
  }else{
    for(let i = 0; i < this.quote.length; ++i){
      let row = this.quote[i];
      for(const key in row){
        let MatchedRow = row;
        console.log(key);
        if(key == "CustName" || key == "AssocID")
        if(row[key].toLowerCase().includes(this.searchTerm2.toLowerCase())){
          console.log(row);
          FilteredData.push(MatchedRow);
          break;
        }
      
      }
    }
    this.FilteredQuote = FilteredData;
    if(this.FilteredQuote.length == 0){
      alert("No Matches");
      this.FilteredQuote = this.quote;
    }
  }
}

 // Add these properties to your component

// Add these properties to your component
startDate: string = '';
endDate: string = '';

// Modify your filtering logic
searchByDate(): void {
  // Add date filtering logic
  if (this.startDate && this.endDate) {
    const startDateTime = new Date(this.startDate).getTime();
    const endDateTime = new Date(this.endDate).getTime();

    this.FilteredQuote = this.quote.filter(row => {
      const quoteDate = new Date(row.QuoteDate).getTime();
      const processDate = new Date(row.ProcessDate).getTime();
      return ((quoteDate >= startDateTime && quoteDate <= endDateTime) || (processDate >= startDateTime && processDate <= endDateTime));
    });
    console.log(this.quote)
  }
}

 

viewQuote(rowID: any, Total: any): void {
  this.isViewQuoteModal = true;
  this.isMainScreen = false;
  const quoteData = {
    quoteID: rowID
  }
  this.http.post('https://phpapicsci467.azurewebsites.net/php_script/ViewQuote.php', quoteData).subscribe((response: any) => {
    this.selectedQuoteDetails = response;
    this.total = Total;
  });
}


closeViewQuoteModal(): void {
  // Close the view quote modal
  this.isViewQuoteModal = false;
  this.isMainScreen = true;
}

Datacheck(): void{
    //Stored Associate Name
    var AssocName = localStorage.getItem('AssocName');
    if(AssocName !== null){
      this.EmpName = JSON.parse(AssocName);
    }
    //Stored Associate ID
    var savedAssoc = localStorage.getItem('CurrentAssoc');
    if(savedAssoc !== null){
      this.savedAssoc = JSON.parse(savedAssoc);
      console.log(savedAssoc);
    }else{
      this.router.navigateByUrl('/');
      setTimeout(function(){
        alert("Error: User not found, please Login");
      }, 200);
    }
  }
}
    <ng-container *ngIf="isMainScreen">
      <div class = "header-container">
        <h1> Sales Associates</h1>
        <h1>Quotes & Purchase Orders</h1>
      </div> 
      <div class = "search-container">
        <div>
            <input (ngModelChange)="ResetAssoc()" type="text" placeholder="Search Associates" [(ngModel)]="searchTerm">
            <button (click)="SearchAssoc()" class="buttons">Search</button>
            <button class="buttons" (click)="AssociateModal()">Add</button>
        </div>
        <div class="Quote-Related">
          <div>
            <input (ngModelChange)="ResetQuote()" type="text" placeholder="Search Quotes" [(ngModel)]="searchTerm2">
            <button class="buttons" (click)="searchQuote()"> Search</button>
            
            <select [(ngModel)]="selectedStatus" (change)="statusFilter()">
              <option [ngValue]="undefined" disabled hidden>Select Status</option>
              <option value="F">Finalized</option>
              <option value="U">Unsanctioned</option>
              <option value="S">Sanctioned</option>
              <option value="O">Order Created</option>
              <option value="A">Show All</option>
            </select>
            <div class = "Date-Container">
              <div>
                <label for="startDate">Start Date:</label>
                <input type="date" id="startDate" [(ngModel)]="startDate">
              </div>
              <div>
                <label for="endDate">End Date:</label>
                <input type="date" id="endDate" [(ngModel)]="endDate">
              </div>
              <button class="buttons" (click)="searchByDate()">Search by Date</button>
            </div>
        </div>  
      </div>
      </div> 
    </ng-container>
      

      
      <div class="add-assoc-container" *ngIf= "isAddAssocModal" >
          <h1>Add a New Associate:</h1>
            <form class="add-assoc-form" (ngSubmit)="addAssoc($event)" #AddForm>
              <div class="form-group">
                <label for="InputName">Name:</label>
                <input name="EmpName" type="text" id="InputName" aria-describedby="nameHelp" placeholder="Enter Name" [(ngModel)]="Name" required/>
              </div>
              <div class="form-group">
                <label for="InputId">ID:</label>
                <input name="EmpId" type="text" id="InputId" aria-describedby="idHelp" placeholder="Enter Employee ID" [(ngModel)]="ID" required/>
              </div>
              <div class= "form-group"> 
                <label for="InputPass">Password:</label>
                <input name="EmpPass" type="text" id="InputPass" aria-describedby="passHelp" placeholder="Enter Password" [(ngModel)]="Password" required>
              </div>
              <div class="form-group">
                  <label for="InputAddress">Address:</label>
                  <input name="EmpAddress" type="text" id="InputAddress" aria-describedby="addressHelp" placeholder="Enter Address" [(ngModel)]="Address" required>
              </div>
              <div class="form-group">
                <label for="InputAdminFlag">Company Role:</label>
                <select name="AdminFlag" [(ngModel)]="AdminFlag">
                  <option [ngValue]="undefined" disabled hidden>Select a Role</option>
                  <option value="Y">Admin</option>
                  <option value="N">Sales Associate</option>
                  <option value="M">Manager</option>
                </select>
              </div>
              <div class="button-container">
                <button type="submit" id="subButton" class="buttons">Submit</button>
                <button (click)="AssociateModal()" class="buttons">Close</button>
              </div>
            </form>
      </div>
    <div class="edit-assoc-container" *ngIf="isEditAssocModal">
      <h1>Edit Associate</h1>
      <form class="edit-assoc-form" (ngSubmit)="editAssoc()" #editForm>
        <div class="form-group">
          <div class="form-group">
            <label for="editedName">Name:</label>
            <br/>
            <input name="EditName" type="text" id="editedName" placeholder="Edit Name" [(ngModel)]="Name" required>
          </div>
          <div class="form-group">
            <label for="editedPass"> Password:</label>
            <br/>
            <input name="EditPassword" type="text" id="editedPass" placeholder="Edit Password" [(ngModel)]="Password" required>
          </div>
          <div class="form-group">
            <label for="editedAddress"> Address:</label>
            <br/>
            <input name="EditAddress" type="text" id="editedAddress" placeholder="Edit Address" [(ngModel)]="Address" required>
          </div>
          <button type="submit" class="buttons">Save Changes</button>
          <button class="buttons" (click)="EditAssociateModal()">Close</button>
        </div>
      </form>
      <button class="buttons" (click)="delAssoc()">Delete Associate</button>
    </div>

    <ng-container *ngIf="isMainScreen">
      <div class="table-container">
        <table class="assoc-table">
            <thead>
                <tr>
                    <td>ID</td>
                    <td>Name</td>
                    <td>Password</td>
                    <td> Sales Commission</td>
                    <td> Address</td>
                </tr>
            </thead>
            <tbody>
                <tr class="Assoc-Rows" *ngFor="let rows of FilteredAssoc" (click)="editAssocModal(rows)">
                    <td>{{rows.ID}}</td>
                    <td>{{rows.Name}}</td>
                    <td>{{rows.Password}}</td>
                    <td>{{rows.SalesCommision | number: '1.2-2'}}</td>
                    <td>{{rows.Address}}</td>
                </tr>
            </tbody>
        </table>   

        <table class="q-table">
            <thead>
                <tr>
                    <td>ID</td>
                    <td>AssocID</td>
                    <td>Customer Name</td>
                    <td> QuoteDate</td>
                    <td> Status</td>
                    <td> Total</td>
                    <td> Discount</td>
                </tr>
            </thead>
            <tbody>
                <tr class="Quote-Rows" *ngFor="let rows of FilteredQuote" (click)="viewQuote(rows.ID, rows.Total)" >
                    <td>{{rows.ID}}</td>
                    <td>{{rows.AssocID}}</td>
                    <td>{{rows.CustName}}</td>
                    <td>{{rows.QuoteDate}}</td>
                    <td>{{rows.Status}}</td>
                    
                    <td>&#36;{{rows.Total}}</td>
                
                    <td>{{rows.Discount}}</td>
                </tr>
            </tbody>
        </table>
      </div>
    </ng-container>

    <div *ngIf="isViewQuoteModal">
      <button class="buttons CloseView" (click)="closeViewQuoteModal()">Close</button>
      <div class="modal-content">
            <!--Code below is  for table-->
            <table class="main-table">
              <!--Table Header-->
              <thead>
                  <tr>
                      <td>Description</td>
                      <td>Quantity</td>
                      <td>Price</td>
                  </tr>
              </thead>
              <!--End of Table Header-->

              <!--Table Body Contents-->
              <tbody id="myTable">
                  <tr *ngFor="let rows of selectedQuoteDetails">
                      <ng-container *ngIf="rows.SecretFlag == 'N'">
                      <td>{{rows.RowDesc}}</td>
                      <td>{{rows.RowQty}}</td>
                      <td>{{rows.RowPrice}}</td>
                  </ng-container>
                  </tr>
              </tbody>
              <!--End of Table Body Contents-->

          <!--Table footer Secret Message-->
          <tfoot class="tFooter">
              <tr *ngFor="let rows of selectedQuoteDetails" >
                  <ng-container *ngIf="rows.SecretFlag == 'Y'">
                  <td>{{rows.RowDesc}}</td>
                  </ng-container>
              </tr>
          </tfoot>
          <tfoot class="tFooter2">
              <tr class="note">
                  <td class="secretLabel" colspan="3">
                      Secret Message:
                  </td>
                  <td></td>
                  <td></td>
              </tr>
          </tfoot>
          <!--End of Table footer Secret Message-->
        </table>
        <!--Prints out Total Price-->
        <div class="row justify-content-center">           
          <div class="QuoteTotal col-auto">
              <h4>Total:</h4>
              <h5>${{ total | number: '1.2-2'}}</h5>
          </div>  
        </div>
      </div>
    </div>
