import { Component, ElementRef, Renderer2, ViewChild, OnInit, Inject } from '@angular/core';
import { Router, NavigationEnd,ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { response } from 'express';
import { event } from 'jquery';


export interface DialogData {
  animal: string;
  name: string;
}

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent {
  constructor(private http: HttpClient, private router: Router) { }
  
  //2D arrays for the 2 tables
  assoc: any[]=[];
  FilteredAssoc: any[] = [];
  quote: any[]=[];
  FilteredQuote: any[] = [];
  
  SalesAssoc: any;
  newAssociate: any = {};
  EmpName: any;
  searchTerm: string = "";
  searchTerm2: string="";
  EditName: any;
  EditPassword: any;
  EditSales: any;
  EditAddress: any;
  add: any;
  edit: any;
  savedAssoc: any;
  
  isAddAssocModal: boolean = false;
  ID: string= "";
  Password: string= "";
  Name: string = "";
  SalesCommision: string = "";
  Address: string= "";
  selectedAssoc: any;
  selectedStatus: any;
  selectedAssocForEdit: any ={};
  isEditAssocModal: boolean = false;
  SelectedAction: string = "";
  selectedQuoteDetails: any;
  isViewQuoteModal: boolean  =false;
  quoteID:any;

  ngOnInit () {
    this.Datacheck();
    this.http.get('https://phpapicsci467.azurewebsites.net/php_script/AssociateTable.php').subscribe((response:any) => {
      this.assoc = response;
      this.FilteredAssoc = this.assoc;
      
      console.log(this.assoc);

    });
  
  this.http.get('https://phpapicsci467.azurewebsites.net/php_script/QuoteTable.php').subscribe((response:any) => {
    this.quote = response;
    this.FilteredQuote = this.quote;
    console.log(this.quote);
  });

  }
  
  addAssociateModal(): void {
    this.isAddAssocModal = true;
  }
  closeAddAssocModal(): void {
    this.isAddAssocModal = false;
  }
  addAssoc(event:Event): void {
    for(let i=0;i<this.assoc.length;++i){ 
        if(this.ID===this.assoc[i]['ID']) {
          alert("this id is in use");
          this.ID="";
          location.reload();
        }
    }
    const AddASSOCData = {
       ID: this.ID,
       Name: this.Name,
       Password: this.Password,
       Address: this.Address
    }
    this.http.post('https://phpapicsci467.azurewebsites.net/php_script/AddAssociate.php', AddASSOCData).subscribe((response:any)=> {
    this.add = response;
    location.reload();

    });
  }

  // Add a new function to open the edit modal
editAssocModal(selectedRow: any): void {
  this.selectedAssocForEdit = {...selectedRow};
  this.isEditAssocModal = true;
  this.ID=this.selectedAssocForEdit['ID'];
  this.Name=this.selectedAssocForEdit['Name'];
  this.Password=this.selectedAssocForEdit['Password'];
  this.Address=this.selectedAssocForEdit['Address'];
  console.log(this.selectedAssocForEdit);
  // Assign the selected associate to the property
}
closeEditAssocModal(): void {
  this.isEditAssocModal = false;
}
  
AssocAction(selectedRow: any): void {
  if(this.SelectedAction === 'Delete'){
  } else if(this.SelectedAction === 'View'){}
  else if(this.SelectedAction ==='Edit'){}
  console.log(selectedRow);
}
// Add a new function to handle the edit action
// In your component
editAssoc(event: Event): void {
  console.log('Selected Assoc for Edit:', this.selectedAssocForEdit);

  // Log the selectedAssocForEdit object
  console.log('Selected Assoc for Edit Object:', JSON.stringify(this.selectedAssocForEdit));

  const FinalAssocData = {
    ID: this.ID,
    Name: this.Name,
    Password: this.Password,
    Address: this.Address
  };
    // Handle the "Edit" button click
    // Update the selected associate using the API endpoint or another method
    console.log(FinalAssocData);
    this.http.post('https://phpapicsci467.azurewebsites.net/php_script/EditAssoc.php', FinalAssocData, { headers: { 'Content-Type': 'application/json' } }).subscribe((response: any) => {
      // Handle the response as needed
      console.log('HTTP Response:', response);

      // Close the edit modal
   location.reload();
    });
    
      console.error('Associate not found for editing.');
    
  }


/*
 * This Function Will Reset the Assoc Table for the Next Search
*/
ResetAssoc(): void {
  this.FilteredAssoc = this.assoc;
}
  
deleteAssocUrl="https://phpapicsci467.azurewebsites.net/php_script/deleteAssociate.php";

delAssoc(id: string): void {

  const confirmation = confirm('Are you sure you want to delete this associate?');
  if (confirmation) {
      // Call your delete API endpoint with the specific ID
      this.http.post('https://phpapicsci467.azurewebsites.net/php_script/deleteAssociate.php', { id }).subscribe((response: any) => {
          if (response.success) {
              // Update your assoc array or refresh the data
              // For example, you can call your API endpoint to get the updated data
              this.http.get('https://phpapicsci467.azurewebsites.net/php_script/AssociateTable.php').subscribe((updatedResponse: any) => {
                 this.assoc = updatedResponse;
              });
            }else {  
              alert('Error deleting associate: ' + response.message);
            }
      });
  }
}



// New method for searching associates
SearchAssoc(): void {
    
  let FilteredData = [];
  this.FilteredAssoc = this.assoc; // Reset to show all data
  if(this.searchTerm === ""){
    this.FilteredAssoc = this.assoc; // Reset to show all data
  }else{
    for(let i = 0; i < this.assoc.length; ++i){
  
      let row = this.assoc[i];
  
      for(const key in row){
        let MatchedRow = row;
        if(row[key].toLowerCase().includes(this.searchTerm.toLowerCase())){
          console.log(row);
          FilteredData.push(MatchedRow);
          break;
        }
      
      }
    }
    this.FilteredAssoc = FilteredData;
  }

}

/*
  Reset Quote Function
*/
ResetQuote(): void{
  this.FilteredQuote = this.quote;
}


// filters quote table by status
statusFilter(): void {
  const url = 'https://phpapicsci467.azurewebsites.net/php_script/StatusFilter.php';
  const params = { status: this.selectedStatus };

  this.http.post(url, params).subscribe((response: any) => {
    this.quote = response;
  });
}

/*
 View Assoc Function
*/
viewAssoc(): void {

}


searchQuote (): void {
  let FilteredData = [];
  this.FilteredQuote = this.quote; // Reset to show all data
  if(this.searchTerm2 === ""){
    this.FilteredQuote = this.quote; // Reset to show all data
  }else{
    for(let i = 0; i < this.quote.length; ++i){
      let row = this.quote[i];
      for(const key in row){
        let MatchedRow = row;
        if(row[key].toLowerCase().includes(this.searchTerm2.toLowerCase())){
          console.log(row);
          FilteredData.push(MatchedRow);
          break;
        }
      
      }
    }
    this.FilteredQuote = FilteredData;
  }
}

 

viewQuote(quoteID: Event): void {

  this.http.get('https://phpapicsci467.azurewebsites.net/php_script/ViewQuote.php').subscribe((response: any) => {
    this.selectedQuoteDetails = response;
    console.log(this.selectedQuoteDetails)
  });
}

// Add these properties to your component class
selectedQuote: any = []; // This will hold the details of the selected quote
ViewQuoteModal(): void {
  this.isViewQuoteModal =true;
}
closeViewQuoteModal(): void {
  // Close the view quote modal
  this.isViewQuoteModal = false;
}

Datacheck(): void{
    //Stored Associate Name
    var AssocName = localStorage.getItem('AssocName');
    if(AssocName !== null){
      this.EmpName = JSON.parse(AssocName);
    }
    //Stored Associate ID
    var savedAssoc = localStorage.getItem('CurrentAssoc');
    if(savedAssoc !== null){
      this.savedAssoc = JSON.parse(savedAssoc);
      console.log(savedAssoc);
    }else{
      //this.router.navigateByUrl('/');
      setTimeout(function(){
        alert("Error: User not found, please Login");
      }, 200);
    }
  }
}
                 

<div class = "header-container">
  <h1> Sales Associates</h1>
  <h1>Quotes & Purchase Orders</h1>
</div> 
<div class = "search-container">
  <div>
      <input (ngModelChange)="ResetAssoc()" type="text" placeholder="Search Associates" [(ngModel)]="searchTerm">
      <button (click)="SearchAssoc()" class="buttons">Search</button>
      <button class="buttons" (click)="addAssociateModal()">Add</button>
  </div>
  <div>
      <input (ngModelChange)="ResetQuote()" type="text" placeholder="Search Quotes" [(ngModel)]="searchTerm2">
      <button class="buttons" (click)="searchQuote()"> Search</button>
  </div>
  <select [(ngModel)]="selectedStatus" (change)="statusFilter()">
    <option value="F">Finalized</option>
    <option value="U">Unsanctioned</option>
    <option value="S">Sanctioned</option>
    <option value="O">Order Created</option>
  </select>
  <button (click)="statusFilter()"> Filter</button>
</div> 
<div class="button-container">
   
    <div *ngIf= "isAddAssocModal" >
        <form (ngSubmit)="addAssoc($event)" #editForm>
           <div class="form-group">
             <label for="InputId">ID:</label>
             <br/>
             <input name="EmpId" type="text" id="InputId" aria-describedby="idHelp" placeholder="Enter Employee ID" [(ngModel)]="ID" required>
           </div>
           <div class="form-group">
             <label for="InputName">Name:</label>
             <br/>
             <input name="EmpName" type="text" id="InputName" aria-describedby="nameHelp" placeholder="Enter Name" [(ngModel)]="Name" required>
           </div>
           <div class= "form-group"> 
             <label for="InputPass">Password:</label>
             <br/>
             <input name="EmpPass" type="text" id="InputPass" aria-describedby="passHelp" placeholder="Enter Password" [(ngModel)]="Password" required>
           </div>
           <div class="form-group">
               <label for="InputAddress">Address:</label>
               <br/>
               <input name="EmpAddress" type="text" id="InputAddress" aria-describedby="addressHelp" placeholder="Enter Address" [(ngModel)]="Address" required>
           </div>
           <button type="submit" id="subButton" class="btn btn-primary">Submit</button>
           <button (click)="closeAddAssocModal()"> close</button>
         </form>
       </div>
</div>
<div *ngIf="isEditAssocModal">
  <form (ngSubmit)="editAssoc($event)" #editForm>
    <div class="form-group">
      <button (click)="closeEditAssocModal()">close </button>
      <h2>Edit Associate</h2>
      <div class="form-group">
        <label for="editedName">Name:</label>
        <br/>
        <input name="EditName" type="text" id="editedName" placeholder="Edit Name" [(ngModel)]="Name" required>
      </div>
      <div class="form-group">
        <label for="editedPass"> Password:</label>
        <br/>
        <input name="EditPassword" type="text" id="editedPass" placeholder="Edit Password" [(ngModel)]="Password" required>
      </div>
      <div class="form-group">
        <label for="editedAddress"> Address:</label>
        <br/>
        <input name="EditAddress" type="text" id="editedAddress" placeholder="Edit Address" [(ngModel)]="Address" required>
      </div>
      <button type="submit">Save Changes</button>
    </div>
  </form>
</div>

<div class="table-container">
  <table class="main-table">
      <thead>
          <tr>
              <td>ID</td>
              <td>Name</td>
              <td>Password</td>
              <td> Sales Commission</td>
              <td> Address</td>
              <td> </td>
          </tr>
      </thead>
      <tbody id="myTable">
          <tr *ngFor="let rows of FilteredAssoc">
              <td>{{rows.ID}}</td>
              <td>{{rows.Name}}</td>
              <td>{{rows.Password}}</td>
              <td>{{rows.SalesCommision | number: '1.2-2'}}</td>
              <td>{{rows.Address}}</td>
              <td>
              <button (click)="editAssocModal(rows)"> Edit</button>
              </td>
              <td>
              <button (click)="delAssoc(rows.ID)"> Delete </button>
              </td>
          </tr>
      </tbody>
  </table>   

  <table class="main-table">
      <thead>
          <tr>
              <td>ID</td>
              <td>AssocID</td>
              <td>CustID</td>
              <td>Customer Name</td>
              <td> QuoteDate</td>
              <td> Status</td>
              <td> Total</td>
              <td> Discount</td>
          </tr>
      </thead>
      <tbody id="myTable">
          <tr *ngFor="let rows of FilteredQuote">
            
              <td>{{rows.ID}}</td>
              <td>{{rows.AssocID}}</td>
              <td>{{rows.CustID}}</td>
              <td>{{rows.CustName}}</td>
              <td>{{rows.QuoteDate}}</td>
              <td>{{rows.Status}}</td>
              <td>{{rows.Total}}</td>
              <td>{{rows.Discount}}</td>
              <td>
                <button class="buttons" (click)="ViewQuoteModal()">View</button>
              </td>
          </tr>
      </tbody>
  </table>
</div>
<div *ngIf="isViewQuoteModal" class="modal">
  <div class="modal-content">
    <h1>hi</h1>
    <form (ngSubmit)="viewQuote($event)" #myForm>
    <button (click)="closeViewQuoteModal()">Close</button>
    <h2>View Quote</h2>
    <div class="form-group">
