import { Component, Inject, ElementRef, Renderer2, ViewChild, OnInit, createComponent } from '@angular/core';
import { Router, NavigationEnd,ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import {
  MatDialog,
  MAT_DIALOG_DATA,
  MatDialogRef,
  MatDialogTitle,
  MatDialogContent,
  MatDialogActions,
  MatDialogClose,
} from '@angular/material/dialog';
import {MatButtonModule} from '@angular/material/button';
import {FormsModule} from '@angular/forms';
import {MatInputModule} from '@angular/material/input';
import {MatFormFieldModule} from '@angular/material/form-field';

@Component({
  selector: 'app-pocreate',
  templateUrl: './pocreate.component.html',
  styleUrls: ['./pocreate.component.css']
})
export class PocreateComponent {
  responseFromPHP: any;
  selectOptions: any[] = [];
  selectedQuote: any = [];
  QuoteLine: any;
  SelectedVal: any;
  EmpName: any;
  savedAssoc: any;
  quoteSelected: boolean = false;
  //Discount and Total Amounts
  total: any = 0.0;
  DiscountType: any = 'P';
  DiscountDollar: any = 0.00;
  DiscountPercent: any = 0.00;
  TempTotal: any = 0;


  constructor(private http: HttpClient, private router: Router) { }

  ngOnInit() {
    this.Datacheck();
    this.http.get('https://phpapicsci467.azurewebsites.net/php_script/selectQuote.php').subscribe((response: any) => {
      this.selectOptions = response;
    });
  }

  Datacheck(): void
  {
    //Stored Associate Name
    var AssocName = localStorage.getItem('AssocName');
    if(AssocName !== null){
      this.EmpName = JSON.parse(AssocName);
    }
    //Stored Associate ID
    var savedAssoc = localStorage.getItem('CurrentAssoc');
    if(savedAssoc !== null){
      this.savedAssoc = JSON.parse(savedAssoc);
      console.log(savedAssoc);
    }else{
      this.router.navigateByUrl('/');
      setTimeout(function(){
        alert("Error: User not found, please Login");
      }, 200);
    }
  }

  private quoteURL='https://phpapicsci467.azurewebsites.net/php_script/selectQuoteLine.php';

  RetriveData(QID: any) : void{
    this.quoteSelected = true;
    for(let i = 0; i < this.selectOptions.length; ++i)
    {
      if(this.selectOptions[i]['ID'] == QID)
      {
        this.total = this.selectOptions[i]['Total'].toLocalStrin('en-US');
        this.selectedQuote = this.selectOptions[i];
      }
    }
    console.log(this.selectedQuote['ID']);
    const quoteData={quoteID: QID}
    this.http.post(this.quoteURL,quoteData, {responseType:'json'}).subscribe(
      responseData=>{
        this.QuoteLine=responseData;
        if (this.QuoteLine['SecretFlag']==='Y')
        {
        }
    });
  }
  count: number=2;

DiscountSelect():void
{
  console.log(this.DiscountType);
}

ChangeCounter: number = 0;

//Code below is for applying discount when the button is click
ApplyDiscount():void
{
  if (this.ChangeCounter < 1)
  {
    this.TempTotal = this.total;
  }
  else
  {
    this.total = this.TempTotal;
  }

  ++this.ChangeCounter;

  if(this.DiscountType == 'P')
  {
    console.log("This is %: " + this.DiscountPercent);
    this.total = this.total - (this.total * this.DiscountPercent);
    if(this.total < 0){
      this.total = this.TempTotal;
      this.DiscountPercent = 0.0;
    }
  }
  else if(this.DiscountType == 'D')
  {
    console.log("This is $: " + this.DiscountDollar);
    this.total = this.total - this.DiscountDollar;
    if(this.total < 0){
      this.DiscountDollar = 0.0;
      this.total = this.TempTotal;
     }
  }
}
QuoteProcessUrl = "https://phpapicsci467.azurewebsites.net/php_script/ProcessingSystem.php";
SubmitFinal(): void{

  const FinalQuoteData = {
    QuoteData: this.selectedQuote,
    NewTotal: this.total

  };

  this.http.post(this.QuoteProcessUrl, FinalQuoteData).subscribe({        
    next: (data: any) => {
    // This shows array of response
    console.log(data);

    //New Amount
    const saleAmt: number = data['amount'];

    //tempPct removes "%" -> Pct creates a float
    const tempPct = data['commission'].replace('%','');
    const Pct = parseFloat(tempPct);

    //getting commission rate
    const assocCom = saleAmt * (Pct / 100);

      //array of new associate data
    const assocData = 
    {
      assoc: data['associate'],
      comAmt: assocCom
    }

    this.UpdateAssoc(assocData);

    },
    error: (error) => {
      console.error('Error Sending data', error);
    }
  });
    console.log(this.selectedQuote);
}


UpdateAssocUrl = "https://phpapicsci467.azurewebsites.net/php_script/UpdateSalesAssoc.php";

UpdateAssoc(assocData:any): void
{
  console.log(assocData);

  this.http.post(this.UpdateAssocUrl, assocData).subscribe({        
    next: (data: any) => {
      console.log(data);
    },
    error: (error) => {
      console.error('Error Sending data', error);
    }
  });
}

changeQuote():void{
  this.quoteSelected = false;
}

}


<html>
    <head>
        <link rel="stylesheet" href="pocreate.component.css">
    </head>
    <body>
        <ng-container *ngIf="quoteSelected">
            <div>
               <button (click)="changeQuote()">Select Another Quote</button>
            </div>
        </ng-container>
        <ng-container *ngIf="!quoteSelected">
            <table class="quote-table">
                <thead>
                    <th>Quote</th>
                    <th>Customer</th>
                    <th>Total</th>
                    <th></th>
                </thead>
                <tbody>
                    <tr *ngFor="let option of selectOptions">
                        <td>{{option.ID }} ({{option.QuoteDate}}): {{option.AssocID}}</td>
                        <td>{{option.CustName}}</td>
                        <td>${{option.Total}}</td>
                        <td><button class="po-button" (click)="RetriveData(option.ID)">Purchase Order</button></td>
                    </tr>
                </tbody>    
            </table>
        </ng-container>

        <ng-container *ngIf="quoteSelected">
            <!--Code below is  for table-->
            <table class="main-table">
                <!--Table Header-->
                <thead>
                    <tr>
                        <td>Description</td>
                        <td>Quantity</td>
                        <td>Price</td>
                    </tr>
                </thead>
                <!--End of Table Header-->

                <!--Table Body Contents-->
                <tbody id="myTable">
                    <tr *ngFor="let rows of QuoteLine">
                        <ng-container *ngIf="rows.SecretFlag == 'N'">
                        <td>{{rows.RowDesc}}</td>
                        <td>{{rows.RowQty}}</td>
                        <td>{{rows.RowPrice}}</td>
                    </ng-container>
                    </tr>
                </tbody>
                <!--End of Table Body Contents-->

            <!--Table footer Secret Message-->
            <tfoot class="tFooter">
                <tr *ngFor="let rows of QuoteLine" >
                    <ng-container *ngIf="rows.SecretFlag == 'Y'">
                    <td>{{rows.RowDesc}}</td>
                    </ng-container>
                </tr>
            </tfoot>
            <tfoot class="tFooter2">
                <tr class="note">
                    <td class="secretLabel" colspan="3">
                        Secret Message:
                    </td>
                    <td></td>
                    <td></td>
                </tr>
            </tfoot>
            <!--End of Table footer Secret Message-->
        </table>
        <!--End of Table-->

        <!--Prints out Total Price-->
        <div class="row justify-content-center">           
            <div class="QuoteTotal col-auto">
                <h4>Total:</h4>
                <h5>${{ total | number: '1.2-2'}}</h5>
            </div>  
        </div>
        <!--End of Total Price-->

        <div class="discount">
            <!--Drop Down Menu-->
            <select class="item2" [(ngModel)]="DiscountType" (ngModelChange)="DiscountSelect()">
                <option value="P">Percentage (%)</option>
                <option value="D">Dollar Amount ($)</option>
            </select>
            <!--End of Drop Down-->

            <!--Code below is for Percentage Option-->
                <ng-container *ngIf="DiscountType == 'P'">
                    <label for="dscnt">Final Discount(%):</label>
                    <input id="dscnt" type="number" step=".01" [(ngModel)]="DiscountPercent" max="1" (ngModelChange)="ApplyDiscount()">
                    <button class="SubButton" (click)="SubmitFinal()" matt-raised-button>Enter</button>
                </ng-container>
            <!--End of Percentage Option-->

            <!--Code below is for Dollar Option-->
                <ng-container *ngIf="DiscountType == 'D'">
                    <label for="dscnt">Final Discount($):</label>
                    <input id="dscnt" type="number" step=".5" [(ngModel)]="DiscountDollar" [max]="total" (ngModelChange)="ApplyDiscount()">
                    <button class="SubButton" (click)="SubmitFinal()" matt-raised-button>Enter</button>
                </ng-container>
            <!--End of Dollar Option-->
            </div>

        </ng-container>
    </body>  
</html>
